    alarmStatus.color = alarmStatus.name === 'critical' ? '#D12730' : '#FAA405';
    alarmIcon = `<svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 18 18" fill="none"><path d="M3.35253 15H14.6475C15.8025 15 16.5225 13.7475 15.945 12.75L10.2975 2.99251C9.72003 1.99501 8.28003 1.99501 7.70253 2.99251L2.05503 12.75C1.47753 13.7475 2.19753 15 3.35253 15ZM9.00003 9.75001C8.58753 9.75001 8.25003 9.41251 8.25003 9.00001V7.50001C8.25003 7.08751 8.58753 6.75001 9.00003 6.75001C9.41253 6.75001 9.75003 7.08751 9.75003 7.50001V9.00001C9.75003 9.41251 9.41253 9.75001 9.00003 9.75001ZM9.75003 12.75H8.25003V11.25H9.75003V12.75Z" fill="${alarmStatus.color}"/></svg>`
}

return `<div style="background:#FFF; padding:2px 6px; border:2px solid ${alarmStatus.color}; border-radius:6px; margin-bottom: 8px; display: flex; justify-content: space-between; gap: 3px">` +
        '<div style="margin-bottom:0px">${entityName}</div>'
        +
            alarmIcon
        +
    '</div>'

