self.onInit = function() {

    self.ctx.varsRegex = /\$\{([^\}]*)\}/g;
    var imageUrl = self.ctx.settings.backgroundImageUrl;
    $("#bgimg").css("display","none");
    //$("#bgimg").attr("src",imageUrl);
    self.ctx.$container.css('background', 'url("'+imageUrl+'") no-repeat');
    self.ctx.$container.css('backgroundSize', 'cover');
    self.ctx.$container.css('backgroundPosition', '100%');
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onDestroy = function() {
}

